INSERT INTO `subpages` (`id`, `pagename`, `text`, `created_at`, `updated_at`) VALUES
(1, 'Lịch sử mĩ thuật thế giới', '<p style="text-align: justify;">Mĩ thuật l&agrave; loại h&igrave;nh nghệ thuật xuất hiện đầu ti&ecirc;n tr&ecirc;n thế giới. Mĩ thuật xuất hiện ngay từ khi con người c&oacute; mặt tr&ecirc;n tr&aacute;i đất. Mĩ thuật ra đời từ thời sơ khai, con người trong thời nguy&ecirc;n thuỷ, vẫn c&ograve;n ăn hang, ở lỗ, săn bắn v&agrave; h&aacute;i lượm. Lịch sử Mĩ thuật c&ugrave;ng với lịch sử thế giới trải qua c&aacute;c thời k&igrave; ph&aacute;t triển v&agrave; c&aacute;c giai đoạn lắng đọng hay t&agrave;n lụi.</p>\r\n\r\n<p style="text-align: justify;"><strong>Mĩ thuật thời Nguy&ecirc;n Thuỷ</strong></p>\r\n\r\n<p style="text-align: justify;">Ở thời k&igrave; n&agrave;y, c&ocirc;ng cụ sản xuất th&ocirc; sơ, đời sống săn bắt h&aacute;i lượm, x&atilde; hội chưa ph&acirc;n chia giai cấp, cuộc sống bầy đ&agrave;n chế độ mẫu hệ.&nbsp;C&aacute;c vết t&iacute;ch Mỹ thuật nguy&ecirc;n thủy t&igrave;m thấy ở Nam &Acirc;u, Ch&acirc;u &Aacute;, Ch&acirc;u Phi.&nbsp;Mỹ thuật ở thời kỳ n&agrave;y tồn tại dưới ba h&igrave;nh thức: hội họa, đi&ecirc;u khắc, kiến tr&uacute;c&nbsp;v&agrave; mang c&aacute;c t&iacute;nh chất sau: Nghệ thuật hang động - Chủ yếu l&agrave; tả thực, phản &aacute;nh ch&acirc;n thực v&agrave; sinh động cuộc sống xung quanh. Giả thiết c&oacute; nguồn gốc xuất hiện từ nhu cầu cuộc sống: do lao động, phục vụ nhu cầu t&iacute;n ngưỡng ma thuật hay để giải tr&iacute;. Thường l&agrave; c&aacute;c h&igrave;nh vẽ th&uacute; vật (b&ograve;, ngựa, hươu...) tr&ecirc;n th&agrave;nh v&agrave; trần hang động v&agrave; ch&acirc;n thực, h&igrave;nh khắc tr&ecirc;n đất s&eacute;t rồi đắp l&ecirc;n th&agrave;nh hang. H&igrave;nh người sinh hoạt nhưng sơ lược, kh&aacute;i qu&aacute;t. D&ugrave;ng m&agrave;u sắc tự nhi&ecirc;n. V&iacute; dụ:&nbsp;h&igrave;nh đ&agrave;n ngựa rừng trong hang Latco.v.v.</p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/a1_1.jpg?itok=jz-b7Ls3" style="width: 480px; height: 320px;" /></p>\r\n\r\n<p style="text-align: justify;">Đi&ecirc;u khắc chủ yếu l&agrave; h&igrave;nh người, đặc biệt mi&ecirc;u tả người phụ nữ, mang &yacute; nghĩa phồn thực, nhấn mạnh những đặc điểm giới t&iacute;nh bằng&nbsp;c&aacute;c tượng tr&ograve;n, ph&ugrave; đi&ecirc;u tr&ecirc;n đ&aacute;. V&iacute; dụ: tượng vệ nữ&nbsp;Wilendoff</p>\r\n\r\n<p style="text-align: justify;"><strong>Mĩ thuật thời Ai Cập Cổ Đại</strong></p>\r\n\r\n<p style="text-align: justify;">Ai Cập cổ đại h&igrave;nh th&agrave;nh tr&ecirc;n bờ Nam d&ograve;ng s&ocirc;ng Nile từ năm 5000 trước C&ocirc;ng Nguy&ecirc;n cho đến năm 300 sau C&ocirc;ng Nguy&ecirc;n, nhưng nền Mỹ thụ&acirc;t Ai Cập cổ đại h&atilde;y được nhắc đến được h&igrave;nh th&agrave;nh trong giai đoạn triều đại Pharoah thứ 2 v&agrave; thứ 3. Mỹ thuật Ai Cập l&agrave; nền Mỹ thuật cổ xưa nhất được tiếp nối ng&agrave;y h&ocirc;m nay - những người Hy Lạp l&agrave; học tr&ograve; của người Ai Cập, m&agrave; nền mỹ thuật Hy Lạp cổ đại lại l&agrave; tổ ti&ecirc;n của nền Mỹ thuật Ch&acirc;u &Acirc;u.<br />\r\n<br />\r\nNền Mỹ thuật Ai Cập cổ đại bao gồm c&aacute;c t&aacute;c phẩm nghệ thuật l&agrave;m bằng gốm sứ, trạm khắc, sơn tranh v&agrave; kiến tr&uacute;c. Kỹ thuật của người Ai Cập tinh xảo, chi tiết v&agrave; c&oacute; t&iacute;nh biểu trưng cao. Phần lớn c&aacute;c t&aacute;c phẩm c&ograve;n đến nay đều l&agrave; từ c&aacute;c lăng mộ - đ&oacute; l&agrave; v&igrave; đối với người Ai Cập, hội họa c&oacute; mối quan hệ mật thiết phục vụ t&iacute;n ngưỡng.</p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/110927kpbimataicap07_0.jpg?itok=htD-ftSQ" style="width: 480px; height: 277px;" /></p>\r\n\r\n<p style="text-align: justify;">C&aacute;c bức tranh tr&ecirc;n giấy chỉ xuất hiện khi người Ai Cập cổ đại quyết định l&agrave;m s&aacute;ch &ldquo;hướng dẫn người chết sống lại để tiếp tục kiếp sau&rdquo;. C&aacute;c học giả phương T&acirc;y gọi đ&oacute; l&agrave; &ldquo;Tử thư&rdquo; hay &ldquo;S&aacute;ch của người chết&rdquo; (Book of the Dead-Livre des morts). Đ&uacute;ng ra, dịch chuẩn tiếng Ai Cập cổ, t&ecirc;n s&aacute;ch phải l&agrave; &ldquo;Từ c&aacute;i chết bước ra ban ng&agrave;y&rdquo; (theo Nhật Chi&ecirc;u - C&acirc;u chuyện văn chương phương Đ&ocirc;ng - Nxb Gi&aacute;o Dục 1998). V&igrave; m&ecirc; t&iacute;n, người Ai Cập cổ đại tin rằng chết chưa phải l&agrave; hết, m&agrave; l&agrave; chuẩn bị chuyển sang kiếp sống kh&aacute;c. Muốn cho việc chuyển kiếp được tr&oacute;t lọt th&igrave; phải bảo quản tốt thi h&agrave;i- do đ&oacute; m&agrave; c&oacute; tục ướp x&aacute;c. Chu đ&aacute;o hơn, người ta c&ograve;n bỏ v&agrave;o quan t&agrave;i những cuốn cẩm nang hướng dẫn cho người chết để sang kiếp sau, người chết c&oacute; thể ra khỏi b&oacute;ng tối địa ngục, vượt sa mạc m&ecirc;nh m&ocirc;ng, tr&aacute;nh được c&aacute;c qu&aacute;i vật, t&igrave;m đ&uacute;ng cửa c&ocirc;ng đường của thần Osiris- vua của địa ngục.</p>\r\n\r\n<p style="text-align: justify;">Tiếp đ&oacute; người chết phải biện minh c&ocirc;ng- tội trước Osiris v&agrave; 42 vị ph&aacute;n quan (đại diện cho 42 quận cổ của Ai Cập). Tr&aacute;i tim của người chết sẽ được Anubis- vị thần chuy&ecirc;n ướp x&aacute;c c&oacute; chiếc đầu ch&oacute; rừng đem c&acirc;n. O&aacute;i oăm thay, &ldquo;quả c&acirc;n&rdquo; lại l&agrave; một chiếc l&ocirc;ng chim đ&agrave; điểu nhẹ như bay do nữ thần c&ocirc;ng minh, ch&iacute;nh trực Maat điều khiển. Thăng bằng tức l&agrave; thiện- người tốt sẽ bất tử v&agrave; sống hạnh ph&uacute;c. Lệch tức l&agrave; &aacute;c- kẻ xấu sẽ lập tức bị hung thần Sobek đầu c&aacute; sấu nuốt chửng.</p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/110927kpbimataicap08_0.jpg?itok=if_x31IJ" style="width: 480px; height: 215px;" /></p>\r\n\r\n<p style="text-align: justify;">Để cho kẻ m&ugrave; chữ cũng c&oacute; thể hiểu. Người ta phải vẽ tranh minh họa. Khoảng 1500 năm trước c&ocirc;ng nguy&ecirc;n (c&aacute;ch đ&acirc;y khoảng 3500 năm), c&aacute;c s&aacute;ch của người chết đ&atilde; được sản xuất nhiều v&agrave; bu&ocirc;n b&aacute;n khắp c&otilde;i Ai Cập (vẫn theo Nhật Chi&ecirc;u- s&aacute;ch đ&atilde; dẫn).</p>\r\n\r\n<p style="text-align: justify;">Vẻ đẹp của những bức tranh tối cổ</p>\r\n\r\n<p style="text-align: justify;">Gọi l&agrave; tối cổ v&igrave; qu&aacute; xa xưa. Bạn h&atilde;y ngẫm m&agrave; xem, 26 thế kỷ trước khi Th&aacute;i Lu&acirc;n- vi&ecirc;n quan Trung Quốc- chế ra giấy th&igrave; người Ai Cập cổ đại đ&atilde; ho&agrave;n th&agrave;nh &ldquo;cuốn s&aacute;ch tối cổ của nh&acirc;n loại&rdquo; rồi (cuốn &ldquo;Ch&acirc;m ng&ocirc;n của Ptahoteb&rdquo;). Người ta c&oacute; thể ch&ecirc; giấy papyrus chưa ho&agrave;n hảo nhưng r&otilde; r&agrave;ng đ&oacute; l&agrave; loại giấy c&oacute; thể viết v&agrave; vẽ l&ecirc;n thoải m&aacute;i.</p>\r\n\r\n<p style="text-align: justify;">Về việc chế tạo s&aacute;ch của người chết, Ai Cập cổ đại quan niệm phải vẽ đẹp v&agrave; dễ hiểu bởi phục vụ người chết l&agrave; ưu ti&ecirc;n số một của x&atilde; hội thời đ&oacute;. Theo họ cuộc sống hiện tại l&agrave; tạm bợ, chỉ c&oacute; kiếp sau- nếu được chuẩn bị tốt- mới l&agrave; vĩnh cửu. Do đ&oacute; cần phải viết v&agrave; vẽ đẹp. Vẽ c&agrave;ng đẹp th&igrave; hiệu quả c&agrave;ng cao.</p>\r\n\r\n<p style="text-align: justify;">Chỉ c&oacute; điều trớ tr&ecirc;u l&agrave; cho người chết chứ kh&ocirc;ng phải cho người sống chi&ecirc;m ngưỡng. Tất nhi&ecirc;n, x&eacute;t về chất lượng nghệ thuật cổ Ai Cập th&igrave; tranh tr&ecirc;n giấy papyrus kh&ocirc;ng được xếp ở h&agrave;ng đỉnh cao như Kim tự th&aacute;p (kiến tr&uacute;c), tượng v&agrave; chạm nổi (đi&ecirc;u khắc), tranh tường (b&iacute;ch họa). C&aacute;c tranh trong &ldquo;s&aacute;ch của người chết&rdquo; chỉ được xếp loại nghệ thuật hạng hai, v&igrave; n&oacute; mang nặng t&iacute;nh trang tr&iacute; với c&aacute;c chỉ dẫn tỉ mỉ, chất lượng biểu cảm chưa đặc sắc. Tuy vậy, c&ocirc;ng bằng m&agrave; n&oacute;i th&igrave; loại tranh n&agrave;y cũng c&oacute; một số gi&aacute; trị ri&ecirc;ng, kh&ocirc;ng thể phủ nhận, đ&aacute;ng được ca ngợi. Mặt giấy papyrus kh&ocirc;ng trắng m&agrave; ng&agrave; ng&agrave;, tạo th&agrave;nh độ nền trung gian rất thuận tiện cho loại h&ograve;a sắc trang tr&iacute; &iacute;t m&agrave;u. Thớ sậy cũng rất gợi cảm (tương tự như nền giấy điệp của tranh Đ&ocirc;ng Hồ- VN).</p>\r\n\r\n<p style="text-align: justify;">Bảng m&agrave;u Ai Cập cổ rất &iacute;t m&agrave;u: chỉ c&oacute; trắng, đen, n&acirc;u, đỏ, xanh c&acirc;y, v&agrave;ng nghệ (đ&ocirc;i khi l&agrave; v&agrave;ng d&aacute;t), xanh ch&agrave;m, nhưng vẫn hấp dẫn, do đậm, nhạt mạnh, phối m&agrave;u kh&eacute;o. V&iacute; dụ: trang phục trắng tinh đ&atilde; t&ocirc;n l&ecirc;n m&agrave;u da b&aacute;nh mật của hai người trợ tế trong tranh &ldquo;Nghi lễ mở miệng&rdquo;.<br />\r\n- M&agrave;u t&ocirc; kh&aacute; tinh tế. Kh&ocirc;ng phải ai cũng c&oacute; nước da n&acirc;u đậm: hai c&ocirc; g&aacute;i c&oacute; da m&agrave;u hồng, thầy quản tế đội mặt nạ Anubis c&oacute; ch&acirc;n tay m&agrave;u v&agrave;ng nghệ. Kh&ocirc;ng phải tất cả đều l&agrave; mảng bẹt: con b&ograve; tế được vờn m&agrave;u ở yếm, đ&ocirc;i vợ chồng đứng trong vườn được vờn m&agrave;u ở vai &aacute;o v&agrave; n&uacute;t buộc ở bụng.</p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/110927kpbimataicap13.jpg?itok=0tcfJbC9" style="width: 480px; height: 360px;" /></p>\r\n\r\n<p style="text-align: justify;"><em>Hai vợ chồng đang ngợi ca thần Osiris</em></p>\r\n\r\n<p style="text-align: justify;">- Bố cục tranh Ai Cập rất chặt chẽ với c&aacute;c khoảng đặc rỗng hợp l&yacute; nhưng kh&ocirc;ng k&eacute;m phần s&aacute;ng tạo, thậm ch&iacute; t&aacute;o bạo do c&aacute;ch ph&acirc;n tầng, ph&acirc;n đoạn v&agrave; nhấn mạnh trọng t&acirc;m c&acirc;u chuyện.<br />\r\n- Nhịp điệu động t&aacute;c l&agrave; c&aacute;ch m&agrave; c&aacute;c nghệ nh&acirc;n v&ocirc; danh ng&agrave;n xưa đ&atilde; l&agrave;m cho tranh papyrus Ai Cập trở n&ecirc;n sinh động. V&iacute; dụ trong tranh &ldquo;Nghi lễ Mở miệng&rdquo;: hai c&ocirc; g&aacute;i đang kh&oacute;c đứng kh&oacute;c ngồi, c&ograve;n hai người trợ tế c&ugrave;ng bước đi nhưng tay giơ, tay hạ cho ta cảm gi&aacute;c về sự nối tiếp của chuyển động.<br />\r\n- N&eacute;t kh&ocirc;ng chỉ l&agrave; đường viền h&igrave;nh thể m&agrave; n&eacute;t c&ograve;n thay đổi m&agrave;u v&agrave; đậm nhạt, c&oacute; l&uacute;c tỉa r&otilde; tinh vi, lại c&oacute; l&uacute;c chỉ gợi tả v&agrave; bu&ocirc;ng lơi. Đặc biệt c&oacute; những tập hợp n&eacute;t vạch-chấm l&agrave;m cho c&aacute;c mảng m&agrave;u đỡ đơn điệu (chấm tr&ecirc;n da b&aacute;o, vạch ngắn xếp h&agrave;ng tr&ecirc;n vai x&aacute;c ướp tập hợp vạch ch&eacute;o tr&ecirc;n v&aacute;y của người quản tế).</p>\r\n\r\n<p style="text-align: justify;"><strong>Mĩ thuật thời Hi Lạp Cổ Đại.</strong></p>\r\n\r\n<p style="text-align: justify;">Mĩ thuật l&agrave; sự s&aacute;ng tạo của nghệ thuật. Nhiều sự ph&aacute;t triển l&agrave; do quan điểm ch&iacute;nh trị v&agrave; triết học của thời đại. Mỗi thời đại mở đường cho những thay đổi thời k&igrave; sau.</p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/111.jpg?itok=AjjQ2r9c" style="width: 480px; height: 299px;" /></p>\r\n\r\n<p style="text-align: justify;">C&oacute; những thay đổi trong nghệ thuật như t&aacute;c phẩm đi&ecirc;u khắc cổ điển m&agrave; kh&ocirc;ng giống như t&aacute;c phẩm đi&ecirc;u khắc cổ xưa đ&oacute; l&agrave; một nỗ lực để mi&ecirc;u tả con người thay v&igrave; n&oacute; l&agrave; một l&yacute; tưởng h&oacute;a của c&aacute;c h&igrave;nh thức con người.</p>\r\n\r\n<p style="text-align: justify;">Trong thời gian cổ xưa c&oacute; mật độ ng&agrave;y c&agrave;ng tăng chủ nghĩa c&aacute; nh&acirc;n. C&aacute; t&iacute;nh v&agrave; độc đ&aacute;o, gần như l&agrave; một nhận thức về một vấn đề ch&iacute;nh trong nghệ thuật v&agrave; triết học. C&ocirc;ng lao to lớn của họ ở chỗ họ đ&atilde; tạo ra được một nền nghệ thuật s&aacute;ng ch&oacute;i với nhiều loại h&igrave;nh, loại thể. Tr&ecirc;n cơ sở đ&oacute;, họ lại tiến h&agrave;nh đ&uacute;c kết th&agrave;nh l&iacute; luận về những bước đi của nghệ thuật, tạo th&agrave;nh điểm tựa, th&agrave;nh sức mạnh trực tiếp cho cuộc đấu tranh v&igrave; tinh thần tiến bộ v&agrave; t&acirc;m hồn cao cả của con người. Đ&aacute;nh gi&aacute; vai tr&ograve; của Hy Lạp cổ đại, Ăngghen đ&atilde; viết: &ldquo;Kh&ocirc;ng c&oacute; c&aacute;i cơ sở do Hy Lạp v&agrave; La M&atilde; x&acirc;y n&ecirc;n th&igrave; kh&ocirc;ng thể c&oacute; Ch&acirc;u &Acirc;u hiện đại&hellip; Những h&igrave;nh thức huy ho&agrave;ng của n&oacute; đ&atilde; dẹp tan những b&oacute;ng ma của thời k&igrave; Trung cổ, ở nước &Yacute; đ&atilde; xuất hiện một thời k&igrave; phồn vinh chưa từng c&oacute; về nghệ thuật&hellip;&rdquo;. Ngay từ buổi đầu lập nghiệp, nh&agrave; nước d&acirc;n chủ chủ n&ocirc; Hy Lạp được tạo n&ecirc;n trước hết bằng c&ocirc;ng luận, bằng c&aacute;ch giơ tay t&aacute;n th&agrave;nh thủ lĩnh. Ch&iacute;nh những nguy&ecirc;n tắc d&acirc;n chủ n&agrave;y đ&atilde; g&oacute;p phần quan trọng khẳng định vai tr&ograve; vị tr&iacute; của con người. Con người được khẳng định về mặt tầm v&oacute;c, tr&iacute; tuệ v&agrave; t&acirc;m hồn. Nhờ vai tr&ograve; t&iacute;ch cực của cuộc sống, của nh&acirc;n d&acirc;n m&agrave; nghệ thuật Hy Lạp đ&atilde; mang t&iacute;nh x&atilde; hội c&ocirc;ng d&acirc;n.</p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/black_figure_vase.jpg?itok=TQDYv-mx" style="width: 320px; height: 273px;" /></p>\r\n\r\n<p style="text-align: justify;">Nghệ thuật n&oacute;i ri&ecirc;ng v&agrave; đời sống thẩm mĩ cổ đại Hy Lạp n&oacute;i chung thấm nhuần l&ograve;ng tin v&agrave;o vẻ đẹp v&agrave; sự cao cả của con người tự do biết đ&oacute;n nhận tr&aacute;ch nhiệm. Những t&aacute;c phẩm của nghệ thuật Hy Lạp cho đến nay vẫn l&agrave;m ch&uacute;ng ta ngạc nhi&ecirc;n về t&iacute;nh hiện thực, t&iacute;nh h&agrave;i h&ograve;a, nh&acirc;n tố ho&agrave;n chỉnh, tinh thần lạc quan anh h&ugrave;ng v&agrave; l&ograve;ng t&ocirc;n trọng phẩm chất của con người. Ở đ&acirc;y, lần đầu ti&ecirc;n vẻ đẹp to&agrave;n diện của con người trở th&agrave;nh l&iacute; tưởng thẩm mĩ trong s&aacute;ng, th&agrave;nh nguồn cảm hứng chủ yếu của văn học nghệ thuật. Hơn ở đ&acirc;u hết, ở Hy Lạp cổ đại, nh&agrave; nước d&acirc;n chủ chủ n&ocirc; vừa đề cao vai tr&ograve; của c&aacute;c v&otilde; tướng như H&eacute;c-to, Asin, lại vừa đề cao vẻ đẹp tr&iacute; tuệ của c&aacute;c nh&agrave; hiền triết như Đ&ecirc;m&ocirc;cr&iacute;t, Aristot&hellip; Vẻ đẹp thể chất của con người được trọng vọng, người ta mở hội đua t&agrave;i ở Olempic, người ta tạc tượng để tưởng niệm những dũng tướng, những người tr&iacute; tuệ uy&ecirc;n b&aacute;c v&agrave; cả dựng tượng, vẽ tranh ngợi ca những người đoạt giải qu&aacute;n qu&acirc;n thể thao.</p>\r\n\r\n<p style="text-align: justify;">Tho&aacute;t ra khỏi thời k&igrave; m&ocirc;ng muội d&atilde; man, con người bắt đầu hướng đ&ocirc;i mắt đầy kh&aacute;t vọng của m&igrave;nh v&agrave;o thế giới của những c&aacute;i cao đẹp. Họ chi&ecirc;m ngưỡng thế giới ấy bằng một trực gi&aacute;c h&igrave;nh tượng, bằng triết học th&ocirc; sơ; họ giải th&iacute;ch cuộc đời bằng cảm quan, nhưng sự cảm quan n&agrave;y đ&atilde; c&oacute; dấu vết của c&aacute;i nh&igrave;n khoa học luận l&iacute;. T&ocirc;n gi&aacute;o của Hy Lạp cổ đại cũng rất kh&aacute;c với c&aacute;c t&ocirc;n gi&aacute;o của c&aacute;c d&acirc;n tộc kh&aacute;c c&ugrave;ng thời.</p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/images_1.jpg?itok=tTQ9l7PI" style="width: 241px; height: 209px;" /></p>\r\n\r\n<p style="text-align: justify;">C&aacute;c vị thần của họ cũng c&oacute; cuộc sống y như con người dưới trần thế. Thần linh cũng cần ăn mặc, cũng th&iacute;ch m&uacute;a h&aacute;t, mở hội, tiệc t&ugrave;ng, cũng y&ecirc;u đương vụng trộm, cũng ghen tu&ocirc;ng, kh&iacute;ch b&aacute;c. Vẻ đẹp của c&aacute;c vị thần cũng kh&ocirc;ng kh&aacute;c l&agrave; mấy so với vẻ đẹp của con người. Ch&iacute;nh v&igrave; thế, thực chất của Thần thoại Hy Lạp l&agrave; sự phối hợp giữa tr&iacute; tuệ v&agrave; hồn thơ, n&oacute;i như C. M&aacute;c, n&oacute; kh&ocirc;ng chỉ l&agrave; &ldquo;L&ograve; ph&aacute;t sinh, m&agrave; c&ograve;n l&agrave; nguồn nu&ocirc;i dưỡng nghệ thuật&rdquo;. Nghệ thuật Hy Lạp cổ đại đ&atilde; th&agrave;nh tựu tr&ecirc;n tất cả c&aacute;c mặt đ&oacute; của đời sống x&atilde; hội. Một ph&iacute;a, n&oacute; trực tiếp m&ocirc; tả cuộc đời c&oacute; thực: những cảnh l&agrave;m ăn, bu&ocirc;n b&aacute;n v&agrave; chiến trận. Ph&iacute;a kia, n&oacute; ngợi ca c&aacute;i thế giới do tr&iacute; tưởng tượng phong ph&uacute; của con người th&ecirc;u dệt n&ecirc;n &ndash; thế giới viễn tưởng, nghĩa l&agrave; một thi&ecirc;n đường tr&ecirc;n đỉnh Olanhpơ. Đồng thời, họ cũng kh&ocirc;ng kh&eacute;o v&agrave; thực tế khi đan kết hai thế giới ấy v&agrave;o nhau. Tuy nhi&ecirc;n trong sự đan kết đ&oacute;, bao giờ con người cũng l&agrave; nh&acirc;n vật trung t&acirc;m, n&eacute;t hi&ecirc;n thực vẫn l&agrave; n&eacute;t chủ đạo. Sự ch&uacute; &yacute; ngợi ca một loạt những mẫu người trong l&iacute; tưởng thẫm mĩ Hy Lạp cổ đại gắn b&oacute; với những ước vọng lớn lao của con người ấy về c&aacute;i đẹp ho&agrave;n thiện, ho&agrave;n mĩ, về một con người to&agrave;n thiện, to&agrave;n b&iacute;ch cả về sức mạnh cơ bắp lẫn sức mạnh t&agrave;i tr&iacute; rời chuyển n&uacute;i s&ocirc;ng. Song song với sự ph&aacute;t triển của nghệ thuật, c&aacute;c ng&agrave;nh khoa học như: triết học, ch&iacute;nh trị học, đạo đức học, khoa học vũ trụ, mỹ học&hellip;cũng được ch&uacute; &yacute;. Sự tạo lập một l&iacute; thuyết mĩ học đ&atilde; c&oacute; t&aacute;c dụng tổng kết đời sống thẩm mĩ v&agrave; c&oacute; t&aacute;c dụng hướng dẫn nghệ thuật ph&aacute;t triển. Từ Pitago đến Đ&ecirc;m&ocirc;crit, từ X&ocirc;crat đến Platon, Aristốt, c&aacute;c quan hệ giữa thực tại v&agrave; thẩm mĩ, c&aacute;i xấu v&agrave; c&aacute;i đẹp, c&aacute;i ch&acirc;n v&agrave; c&aacute;i thiện lu&ocirc;n được đặt ra song song v&agrave; h&ograve;a nhập với c&aacute;c vấn đề của vũ trụ v&agrave; nhận sinh. Kh&ocirc;ng những c&oacute; t&aacute;c dụng trực tiếp ở thời đại đ&oacute;, c&aacute;c l&iacute; thuyết thẩm mĩ của Hy Lạp cổ đại c&ograve;n đ&oacute;ng g&oacute;p v&agrave;o việc mở đường cho những t&igrave;m t&ograve;i của c&aacute;c thời đại kế tiếp l&agrave;m cho sự ph&aacute;t triển của x&atilde; hội lo&agrave;i người th&ecirc;m phong ph&uacute;.</p>\r\n\r\n<p style="text-align: justify;"><strong>Mĩ thuật thời La M&atilde; Cổ Đại</strong></p>\r\n\r\n<p style="text-align: justify;">La M&atilde; cổ đại&nbsp;hay&nbsp;Rome cổ đại&nbsp;l&agrave; một nền văn minh phồn thịnh, bắt đầu tr&ecirc;n b&aacute;n đảo &Yacute;&nbsp;từ thế kỉ 8 trước C&ocirc;ng nguy&ecirc;n. Trải d&agrave;i qua Địa Trung Hải c&ugrave;ng với trung t&acirc;m l&agrave; Roma, La M&atilde; cổ đại l&agrave; một trong những nền văn minh lớn nhất thế giới trong thời k&igrave; cổ đại.&nbsp;Trong suốt 12 thế kỉ tồn tại của nền văn minh, qua c&aacute;c cuộc chinh chiến v&agrave; đồng h&oacute;a, La M&atilde; cổ đại đ&atilde; thống trị c&aacute;c khu vực Nam &Acirc;u, T&acirc;y &Acirc;u, Tiểu &Aacute;, Bắc Phi&nbsp;v&agrave; một phần của Đ&ocirc;ng &Acirc;u. Đ&acirc;y l&agrave; nền văn minh quyền lực nhất tr&ecirc;n l&atilde;nh thổ Địa Trung Hải.</p>\r\n\r\n<p style="text-align: justify;">C&oacute; rất nhiều thể loại hội họa của thời k&igrave; đầu La M&atilde;&nbsp;chịu ảnh hưởng theo lối thời Etruscan, đặc biệt ở c&aacute;c truyền thống hội họa c&ocirc;ng cộng. V&agrave;o thế kỷ thứ 3 TCN, hội họa Hi Lạp&nbsp;ảnh hưởng l&ecirc;n La M&atilde; do chiến tranh v&agrave; chiến lợi phẩm mang lại. Rất nhiều gia đ&igrave;nh ở La M&atilde; đ&atilde; treo c&aacute;c bước tranh phong cảnh của c&aacute;c họa sỹ Hy Lạp.</p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/rome_primaporta.jpg?itok=tKkmvXFs" style="width: 365px; height: 480px;" /></p>\r\n\r\n<p style="text-align: justify;">Nghệ thuật đi&ecirc;u khắc của La M&atilde; thể hiện những người trai trẻ với vẻ đẹp c&acirc;n đối cổ điển, về sau mở ra trường ph&aacute;i pha trộn chủ nghĩa hiện thực v&agrave; chủ nghĩa duy t&acirc;m.</p>\r\n\r\n<p style="text-align: justify;">Nghệ thuật ph&aacute;t triển mạnh mẽ trong thời gian n&agrave;y, v&agrave; thường được sử dụng bởi những người gi&agrave;u c&oacute; v&agrave; mạnh mẽ để tưởng nhớ h&agrave;nh động v&agrave; di sản của họ.&nbsp;Sinh ra từ nghệ thuật Hy Lạp,&nbsp;Người La M&atilde; ngưỡng mộ nền văn h&oacute;a v&agrave; nghệ thuật Hy Lạp.&nbsp;Sau khi chinh phục Hy Lạp, họ mang lại nhiều nghệ sĩ Hy Lạp đến Rome để l&agrave;m cho t&aacute;c phẩm đi&ecirc;u khắc cho họ trong thời trang Hy Lạp.&nbsp;Nghệ thuật Hy Lạp cổ đại đ&atilde; c&oacute; một ảnh hưởng lớn đến nghệ thuật của Rome cổ đại. Mặc d&ugrave; nghệ thuật Hy Lạp đ&atilde; c&oacute; ảnh hưởng lớn nhất tr&ecirc;n người La M&atilde;, nền văn minh kh&aacute;c m&agrave; họ chinh phục v&agrave; gặp phải tr&ecirc;n đế chế rộng của họ cũng c&oacute; ảnh hưởng.&nbsp;Trong đ&oacute; c&oacute; Ai Cập cổ đại, nghệ thuật Đ&ocirc;ng, người Đức, v&agrave; c&aacute;c Celtics.&nbsp;La M&atilde; đi&ecirc;u khắc&nbsp;La M&atilde; đi&ecirc;u khắc đ&oacute;ng một phần quan trọng của cuộc sống h&agrave;ng ng&agrave;y La M&atilde;.&nbsp;T&aacute;c phẩm đi&ecirc;u khắc đ&atilde; lấy mẫu của bức tượng đầy đủ, tượng b&aacute;n th&acirc;n (t&aacute;c phẩm đi&ecirc;u khắc của chỉ đầu của một con người), ph&ugrave; đi&ecirc;u (t&aacute;c phẩm đi&ecirc;u khắc m&agrave; l&agrave; một phần của một bức tường), v&agrave; chiếc qu&aacute;ch (t&aacute;c phẩm đi&ecirc;u khắc tr&ecirc;n ng&ocirc;i mộ). Người La M&atilde; cổ đại được trang tr&iacute; với t&aacute;c phẩm đi&ecirc;u khắc ở một số nơi trong đ&oacute; c&oacute; c&ocirc;ng tr&igrave;nh c&ocirc;ng cộng, c&ocirc;ng vi&ecirc;n, v&agrave; nh&agrave; ri&ecirc;ng v&agrave; khu vườn.&nbsp;đi&ecirc;u khắc La M&atilde; đ&atilde; bị ảnh hưởng nặng nề bởi c&aacute;c t&aacute;c phẩm đi&ecirc;u khắc Hy Lạp.&nbsp;Trong thực tế, rất nhiều c&aacute;c t&aacute;c phẩm đi&ecirc;u khắc La M&atilde; chỉ l&agrave; bản sao của t&aacute;c phẩm đi&ecirc;u khắc Hy Lạp.&nbsp;Người La M&atilde; gi&agrave;u c&oacute; trang tr&iacute; ng&ocirc;i nh&agrave; lớn của họ với t&aacute;c phẩm đi&ecirc;u khắc.Rất nhiều lần những t&aacute;c phẩm đi&ecirc;u khắc l&agrave; của bản th&acirc;n hay tổ ti&ecirc;n của họ.&nbsp;Đối tượng phổ biến kh&aacute;c cho t&aacute;c phẩm đi&ecirc;u khắc bao gồm c&aacute;c vị thần v&agrave; nữ thần, triết gia, vận động vi&ecirc;n nổi tiếng, v&agrave; c&aacute;c tướng th&agrave;nh c&ocirc;ng.&nbsp;</p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/tumblr_madcv7tgsc1rui49ao1_1280_1.jpg?itok=WLsQK-Fg" style="width: 463px; height: 480px;" /><br />\r\nC&aacute;c bức tường của ng&ocirc;i nh&agrave; của người La M&atilde; gi&agrave;u c&oacute; thường được trang tr&iacute; bằng những bức tranh. Những bức tranh n&agrave;y l&agrave; những bức b&iacute;ch họa vẽ trực tiếp tr&ecirc;n tường.&nbsp;Hầu hết c&aacute;c bức tranh đ&atilde; bị ph&aacute; hủy theo thời gian, nhưng một số người trong số họ đ&atilde; được bảo quản trong th&agrave;nh phố Pompeii khi n&oacute; được ch&ocirc;n v&ugrave;i bởi sự phun tr&agrave;o của n&uacute;i lửa.</p>\r\n\r\n<p style="text-align: justify;">Người La M&atilde; cũng l&agrave;m h&igrave;nh ảnh từ gạch m&agrave;u gọi l&agrave; khảm.&nbsp;C&aacute;c khảm c&oacute; thể tồn tại với thời gian tốt hơn so với c&aacute;c bức tranh. Gh&eacute;p c&oacute; thể thực hiện tr&ecirc;n một bức tường, nhưng cũng c&oacute; thể để trang tr&iacute; s&agrave;n. Sau thời Trung cổ, c&aacute;c nghệ sĩ của thời kỳ Phục hưng đ&atilde; nghi&ecirc;n cứu t&aacute;c phẩm đi&ecirc;u khắc, kiến tr&uacute;c v&agrave; nghệ thuật của Rome cổ đại v&agrave; Hy Lạp để truyền cảm hứng cho họ.&nbsp;Nghệ thuật cổ điển của người La M&atilde; đ&atilde; c&oacute; một ảnh hưởng đ&aacute;ng kể về nghệ thuật trong nhiều năm.</p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/tumblr_m9rnm1gp521rui49ao1_1280.jpg?itok=dFrMdcrL" style="width: 480px; height: 478px;" /></p>\r\n\r\n<p style="text-align: justify;"><strong>Mĩ thuật thời Trung Đại&nbsp;</strong></p>\r\n\r\n<p style="text-align: justify;">Nghệ thuật trong thời Trung Cổ đ&atilde; thấy nhiều thay đổi đến sự xuất hiện của thời kỳ Phục hưng sớm.&nbsp;M&ocirc;n nghệ thuật cổ xưa ban đầu bị giới hạn trong sản xuất sơn Pietistic (nghệ thuật t&ocirc;n gi&aacute;o, nghệ thuật Kit&ocirc; gi&aacute;o) theo h&igrave;nh thức bản thảo được chiếu s&aacute;ng, v&agrave; c&aacute;c bức tranh khảm ngo&agrave;i trời trong nh&agrave; thờ.</p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/monreale_photo_ru_sibeaster14.jpg?itok=RI8GEfKx" style="width: 382px; height: 480px;" /></p>\r\n\r\n<p style="text-align: justify;">Sau thời k&igrave; Trung Đại, c&aacute;c nghệ sĩ t&aacute;ch ra khỏi ảnh hưởng của Byzantium v&agrave; phong c&aacute;ch nghệ thuật La M&atilde; để ph&aacute;t triển th&agrave;nh nghệ thuật Gothic rất trực quan.&nbsp;C&aacute;c nghệ sĩ v&agrave; họa sĩ đ&atilde; s&aacute;ng lập phong tr&agrave;o hướng tới chủ nghĩa hiện thực lớn hơn l&ecirc;n đến cực điểm trong phong c&aacute;ch nghệ thuật thời Phục hưng.</p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/feast.jpg?itok=gkVo072X" style="width: 480px; height: 338px;" /></p>\r\n\r\n<p style="text-align: justify;"><strong>Mĩ thuật thời Phục Hưng</strong></p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/p17_virginandchildwithst-1_0.jpg?itok=6Ljux-PX" style="width: 312px; height: 480px;" /></p>\r\n\r\n<p style="text-align: justify;"><em>Sự trinh trắng v&agrave; trẻ thơ với th&aacute;nh Anna: Virgin and Child with St. Anne, 1510, của Leonardo</em></p>\r\n\r\n<p style="text-align: justify;">Trong c&aacute;c giai đoạn tr&ecirc;n, giai đoạn mĩ thuật thời Phục Hưng l&agrave; giai đoạn c&oacute; sự kế thừa, ph&aacute;t triển, do đ&oacute; mĩ thuật Phục Hưng Italia đ&atilde; sản sinh ra nhiều hoạ sĩ nổi tiếng c&oacute; những cống hiến to lớn cho nền mĩ thuật thế giới, trong đ&oacute; c&oacute; ba hoạ sĩ ti&ecirc;u biểu như L&ecirc;-&ocirc;-na đờ Vanhxi, Mi-ken-lăng-giơ, Ra-pha-en, c&oacute; nhiều t&aacute;c phẩm nổi tiếng nhất thế giới.</p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/p3_creationofadam_michelangelo_0.jpg?itok=JWDJu1yR" style="width: 480px; height: 251px;" /></p>\r\n\r\n<p style="text-align: justify;"><em>Sự s&aacute;ng tạo ra lo&agrave;i người, Creation of Adam, 1511 của Michelangelo</em></p>\r\n\r\n<p style="text-align: justify;">V&agrave; l&agrave; giai đoạn Mĩ thuật được đ&aacute;nh gi&aacute; hưng thịnh nhất trong c&aacute;c thời k&igrave;. Mĩ thuật giai đoạn n&agrave;y phải kể đến những t&igrave;m t&ograve;i mới lạ, đem đến một luồng kh&iacute; mới, ch&agrave;o lưu mới cho c&aacute;c hoạ sĩ tr&ecirc;n thế giới v&agrave; nhất l&agrave; c&aacute;c hoạ sĩ ở &Yacute; về c&aacute;c lĩnh vực như&shy;: Hội Hoạ, Đi&ecirc;u Khắc, Kiến Tr&uacute;c...</p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/20101224085932914_0.jpg?itok=aAwI6Hoo" style="width: 340px; height: 480px;" /></p>\r\n\r\n<p style="text-align: justify;"><em>Đức mẹ đồng trinh của Raphael</em></p>\r\n\r\n<p style="text-align: justify;"><em><strong>V&agrave;i n&eacute;t kh&aacute;i qu&aacute;t về Phục Hưng</strong></em></p>\r\n\r\n<p style="text-align: justify;">Thời kỳ Phục Hưng c&oacute; gốc từ tiếng Ph&aacute;p <strong>Renaissance</strong> (nghĩa l&agrave; sự t&aacute;i sinh), c&ograve;n gọi l&agrave; <em>Rinascimento</em> (tiếng &Yacute;), l&agrave; cuộc t&aacute;i sinh c&aacute;c gi&aacute; trị nghệ thuật, tư tưởng, khoa học của thời k&igrave; Cổ đại v&agrave; sự sống lại, ph&aacute;t triển rực rỡ của nền văn minh phương T&acirc;y. Phong tr&agrave;o Phục Hưng thường được coi bắt đầu từ khoảng thế kỉ 14 tại &Yacute; v&agrave; thế kỉ 16 tại Bắc &Acirc;u. N&oacute; cũng được coi l&agrave; đ&aacute;nh dấu giai đoạn chuyển tiếp của ch&acirc;u &Acirc;u từ Thời kỳ Trung cổ sang Thời kỳ Cận đại, cũng như từ Thời kỳ phong kiến sang Thời kỳ tư bản.</p>\r\n\r\n<p style="text-align: justify;">Thuật ngữ Rinascenza (t&aacute;i sinh) được nh&agrave; sử học Giorgio Vasari d&ugrave;ng ban đầu v&agrave;o năm 1550 để chỉ sự hồi sinh v&agrave; ph&aacute;t triển rực rỡ c&aacute;c hoạt động nghệ thuật v&agrave; khoa học bắt đầu tại &Yacute; v&agrave;o thế kỷ 13. Sau đ&oacute;, thuật ngữ Renaissance được Jules Michelet d&ugrave;ng trong tiếng Ph&aacute;p v&agrave; nh&agrave; sử học Thụy Sỹ Jacob Burckhardt ph&aacute;t triển (khoảng những năm 1860). T&aacute;i sinh ở đ&acirc;y c&oacute; hai nghĩa: một l&agrave; sự kh&aacute;m ph&aacute; lại c&aacute;c s&aacute;ch vở cổ điển v&agrave; đem ứng dụng v&agrave;o trong khoa học v&agrave; nghệ thuật; hai l&agrave; để chỉ kết quả của c&aacute;c hoạt động văn h&uacute;a đ&oacute; mang lại sự hồi sinh cho văn h&uacute;a ch&acirc;u &Acirc;u n&oacute;i chung. Như vậy Phục Hưng c&oacute; thể hiểu theo hai c&aacute;ch ch&iacute;nh tuy kh&aacute;c biệt nhưng đều c&oacute; &yacute; nghĩa l&agrave; sự t&aacute;i sinh của nền gi&aacute;o dục cổ điển T&acirc;y phương th&ocirc;ng qua s&aacute;ch vở, t&agrave;i liệu kinh điển của phương T&acirc;y v&agrave; hồi sinh của văn h&oacute;a ch&acirc;u &Acirc;u n&oacute;i chung. Từ H&aacute;n - Việt viết hoa Phục hưng, hay Phục Hưng, l&agrave; thuật ngữ tương đương với kh&aacute;i niệm n&agrave;y.<br />\r\nThời kỳ Phục Hưng được gọi như thế v&igrave; đặc t&iacute;nh cơ bản của thời kỳ n&agrave;y l&agrave; sự hồi sinh của tinh thần thời kỳ Cổ đại. Chủ nghĩa Nh&acirc;n văn ch&iacute;nh l&agrave; phong tr&agrave;o tinh thần cơ bản của thời kỳ n&agrave;y. Việc hồi sinh thể hiện ở chỗ nhiều yếu tố của tư tưởng thời kỳ Cổ đại được t&aacute;i kh&aacute;m ph&aacute; v&agrave; sống lại (văn học, tượng đ&agrave;i kỷ niệm, t&aacute;c phẩm đi&ecirc;u khắc, triết học,...v&agrave; hơn cả đ&oacute; l&agrave; hội hoạ).</p>\r\n\r\n<p style="text-align: justify;">Ti&ecirc;n đề cho tư tưởng mới của thời kỳ Phục Hưng l&agrave; những &yacute; tưởng tự tin của c&aacute;c nh&agrave; thơ người &Yacute; của thế kỷ 14 như Francesco Petrarca, người th&ocirc;ng qua c&aacute;c nghi&ecirc;n cứu rộng lớn về c&aacute;c nh&agrave; văn thời kỳ Cổ đại v&agrave; với Chủ nghĩa C&aacute; nh&acirc;n của &ocirc;ng đ&atilde; cổ động cho niềm tin về gi&aacute; trị của sự đ&agrave;o tạo nh&acirc;n văn v&agrave; ủng hộ cho việc nghi&ecirc;m cứu về ng&ocirc;n ngữ, văn học, lịch sử v&agrave; triết học b&ecirc;n ngo&agrave;i quan hệ với t&ocirc;n gi&aacute;o.</p>\r\n\r\n<p style="text-align: justify;">Ảnh hưởng của những học giả n&oacute;i tiếng Hy Lạp cũng rất đ&aacute;ng kể. Một số học giả đến &Yacute; trong thế kỷ 13 v&agrave; thế kỷ 14 từ Đế quốc Byzantin. Đặc biệt l&agrave; sau khi người Thổ Nhĩ Kỳ chinh phục Constantinople v&agrave;o năm 1453 th&igrave; c&agrave;ng c&oacute; nhiều học giả đến Venezia (tiếng Anh: Venice) v&agrave; những th&agrave;nh phố &Yacute; kh&aacute;c, những người đ&atilde; mang theo kiến thức về nền văn h&uacute;a thời Cổ đại đ&atilde; được lưu trữ gần 1.000 năm trong Đế quốc Byzantin sau khi Đế quốc T&acirc;y La M&atilde; suy t&agrave;n. Cho đến năm 1400 Homer, Herodot, Platon v&agrave; Aristoteles vẫn c&ograve;n được rất nhiều người nhắc đến trong Đế quốc Byzantin. Một v&agrave;i năm trước khi Đế quốc Byzantin sụp đổ, Giovanni Aurispa đ&atilde; đến Constantinople v&agrave; mang về &Yacute; tr&ecirc;n 200 bản viết tay c&aacute;c t&aacute;c phẩm văn học ngoại đạo.</p>\r\n\r\n<p style="text-align: justify;">Trong một nghĩa rộng, người ta hiểu Phục Hưng l&agrave; sự hồi sinh của thời kỳ Cổ đại với c&aacute;c ảnh hưởng của thời kỳ n&agrave;y đến khoa học, văn học, x&atilde; hội, cuộc sống của những tầng lớp thượng lưu v&agrave; sự ph&aacute;t triển của con người đi đến tự do c&aacute; nh&acirc;n ngược lại với chế độ đẳng cấp của thời kỳ Trung cổ. Trong nghĩa hẹp hơn, Phục Hưng l&agrave; một thời kỳ của lịch sử nghệ thuật &ndash; &ldquo;<em>thời k&igrave; của hội hoạ</em>&quot;.</p>\r\n\r\n<p style="text-align: justify;"><img alt="" src="http://www.daotaomythuat.com/sites/daotaomythuat.com/files/styles/large/public/t1l6.mxkhdxxaq8hrx_114326.jpg?itok=10Ea0sFJ" style="width: 480px; height: 335px;" /></p>\r\n\r\n<p style="text-align: justify;"><em><strong>V&agrave;i n&eacute;t kh&aacute;i qu&aacute;t về mĩ thuật Phục Hưng I-ta-li-a</strong></em></p>\r\n\r\n<p style="text-align: justify;">Ở ch&acirc;u &Acirc;u thế kỉ XI, những th&agrave;nh thị được h&igrave;nh th&agrave;nh đ&atilde; ph&aacute; vỡ c&aacute;c l&atilde;nh địa phong kiến, từ đ&oacute; xuất hiện tầng lớp thị d&acirc;n gi&agrave;u c&oacute; - đ&acirc;y l&agrave; tiền th&acirc;n của giai cấp tư sản. Tại I-tali- a, nhiều th&agrave;nh thị trung t&acirc;m ổn định về ch&iacute;nh trị, ph&aacute;t triển về kinh tế, &hellip;nhu cầu đời sống tinh thần được n&acirc;ng cao, giai cấp tư sản muốn c&oacute; một nền văn ho&aacute; chống lại giai cấp phong kiến, đ&oacute; l&agrave; nguy&ecirc;n nh&acirc;n sự ra đời của văn ho&aacute; Phục hưng ở I-ta-li-a, sau lan sang một số nước ở ch&acirc;u &Acirc;u như: Ph&aacute;p, Đức, &hellip; Phong tr&agrave;o mĩ thuật Phục hưng ở I-ta-li-a được khởi đầu v&agrave;o cuối thế kỉ XIII bởi hai hoạ sĩ Xi-ma-bu-&ecirc; v&agrave; Gi-ốt-tụ, phong tr&agrave;o ra đời nhằm kh&ocirc;i phục v&agrave; l&agrave;m hưng thịnh lại nền văn ho&aacute; cổ đại Hi-Lạp, La - M&atilde; (nền văn ho&aacute; đề cao gi&aacute; trị vật chất v&agrave; tinh thần của con người) m&agrave; thời Trung cổ đ&atilde; huỷ hoại; đưa c&aacute;i đẹp phục vụ cuộc sống con người, đồng thời n&acirc;ng cao hơn trong ho&agrave;n cảnh mới để đạt tới sự mẫu mực, ho&agrave;n chỉnh. Sang thế kỉ XIV đến giữa thế kỉ XV, phong tr&agrave;o mĩ thuật Phục hưng ở I-ta-li- a ph&aacute;t triển rực rỡ tr&ecirc;n cơ sở những ph&aacute;t minh khoa học: t&igrave;m ra luật viễn cận, t&igrave;m ra chất liệu sơn dầu, &hellip;C&aacute;c hoạ sĩ thời Phục hưng thường lấy đề t&agrave;i t&ocirc;n gi&aacute;o để thể hiện c&aacute;i đẹp, để diễn tả cuộc sống, diễn tả con người, họ kh&ocirc;ng vẽ theo c&ocirc;ng thức g&ograve; b&oacute; như nghệ thuật Trung cổ (tranh thời Trung cổ mang t&iacute;nh trang tr&iacute; hơn t&iacute;nh tạo h&igrave;nh, chỉ diễn tả kh&ocirc;ng gian hai chiều n&ecirc;n kh&ocirc;ng diễn tả được chiều s&acirc;u; h&igrave;nh d&aacute;ng con người thường c&ograve;m nhom, ốm yếu, thiếu sức sống&hellip;) m&agrave; học hỏi c&aacute;i đẹp từ thời Hy Lạp, La M&atilde;, từ thi&ecirc;n nhi&ecirc;n. C&aacute;c hoạ sĩ đưa kh&ocirc;ng gian thấu thị v&agrave;o tranh v&agrave; &aacute;p dụng những luật v&ograve;m khối theo s&aacute;ng tối, nhờ đ&oacute; con người v&agrave; thi&ecirc;n nhi&ecirc;n được diễn tả rất s&acirc;u về khối, t&igrave;nh cảm, y phục v&agrave; bối cảnh, c&aacute;c qui luật về bố cục, m&agrave;u sắc kh&ocirc;ng gian, tỉ lệ, &aacute;nh s&aacute;ng đến c&aacute;ch diễn tả đều đạt tới sự ho&agrave;n hảo.</p>\r\n', '2015-01-03 13:54:12', '2015-01-03 14:54:40');
INSERT INTO `subpages` (`id`, `pagename`, `text`, `created_at`, `updated_at`) VALUES
(2, 'Điều khoản sử dụng', '<p style="text-align: justify;">Điều khoản sử dụng n&agrave;y l&agrave; những quy định về mối quan hệ v&agrave; tr&aacute;ch nhiệm của ch&uacute;ng t&ocirc;i (chủ website) v&agrave; người truy cập trong việc cung cấp v&agrave; sử dụng c&aacute;c th&ocirc;ng tin, dịch vụ tr&ecirc;n trang web n&agrave;y.</p>\r\n\r\n<p style="text-align: justify;"><strong>Giải th&iacute;ch thuật ngữ</strong></p>\r\n\r\n<p style="text-align: justify;">&quot;Ch&uacute;ng t&ocirc;i&quot; l&agrave; nh&oacute;m sinh vi&ecirc;n lớp 2C-11, khoa C&ocirc;ng nghệ Th&ocirc;ng tin - Trường Đại học H&agrave; Nội (FIT - HANU).<br />\r\n&quot;Người truy cập&quot; l&agrave; những c&aacute; nh&acirc;n, tổ chức, hoặc bất kỳ ai truy cập v&agrave; sử dụng trang web của ch&uacute;ng t&ocirc;i dưới mọi h&igrave;nh thức.<br />\r\nĐ&acirc;y l&agrave; trang website giới thiệu về c&aacute;c bức tranh nổi tiếng thế giới, được x&acirc;y dựng dưới sự dẫn dắt của giảng vi&ecirc;n H&agrave; Quang Minh.<br />\r\n&quot;Điều khoản&quot; l&agrave; những quy định v&agrave; điều kiện m&agrave; người truy cập trang web cần tu&acirc;n theo. Người truy cập &amp; sử dụng Website được mặc nhi&ecirc;n hiểu rằng đ&atilde; đọc v&agrave; hiểu r&otilde; những quy định sử dụng của Website, đồng &yacute; với c&aacute;c quy định được đặt ra một c&aacute;ch tự nguyện.</p>\r\n\r\n<p style="text-align: justify;"><strong>Điều khoản miễn tr&aacute;ch đối với ch&uacute;ng t&ocirc;i</strong></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">&bull; Ch&uacute;ng t&ocirc;i đảm bảo rằng, mọi th&ocirc;ng tin v&agrave; dữ liệu li&ecirc;n quan tới c&aacute;c sản phẩm dịch vụ, tiện &iacute;ch hoặc c&aacute;c nội dung kh&aacute;c (sau đ&acirc;y gọi l&agrave; &ldquo;Th&ocirc;ng tin&rdquo;) tại Trang web, được cung cấp với mức độ tin cậy cao nhất, tuy nhi&ecirc;n c&aacute;c th&ocirc;ng tin chỉ c&oacute; gi&aacute; trị tham khảo đối với người sử dụng. V&agrave; như vậy, ch&uacute;ng t&ocirc;i kh&ocirc;ng phải chịu bất cứ tr&aacute;ch nhiệm g&igrave; đối với bất kỳ ai li&ecirc;n quan đến việc sử dụng c&aacute;c th&ocirc;ng tin c&oacute; tr&ecirc;n Website.</span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">&bull; Đối với những Th&ocirc;ng tin được cung cấp tại Trang web, người sử dụng cần tự ph&acirc;n loại cho ph&ugrave; hợp với từng mục đ&iacute;ch cụ thể trước khi đưa ra quyết định sử dụng hoặc những quyết định c&oacute; li&ecirc;n quan. Người sử dụng sẽ tự chịu mọi rủi ro trong việc sử dụng Th&ocirc;ng tin của Trang web.</span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">&bull; Ch&uacute;ng t&ocirc;i sẽ kh&ocirc;ng chịu tr&aacute;ch nhiệm ph&aacute;p l&yacute; đối với những hư hại hay thương tổn ph&aacute;t sinh c&oacute; li&ecirc;n quan hoặc xuất ph&aacute;t từ việc sử dụng hoặc kh&ocirc;ng sử dụng Trang web, bao gồm cả những hư hại trực tiếp, gi&aacute;n tiếp như mất hoặc hư hỏng dữ liệu; mất hoặc hư hỏng, trục trặc m&aacute;y t&iacute;nh; &hellip;</span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">&bull; Kh&ocirc;ng c&oacute; nội dung n&agrave;o trong thỏa thuận n&agrave;y c&oacute; thể chắc chắn rằng, việc Người d&ugrave;ng sử dụng c&aacute;c Th&ocirc;ng tin, dịch vụ do ch&uacute;ng t&ocirc;i cung cấp tại Trang web sẽ kh&ocirc;ng vi phạm quyền ph&aacute;t minh, sở hữu hay sở hữu tr&iacute; tuệ hoặc vi phạm c&aacute;c quy định kh&aacute;c. V&igrave; thế, ch&uacute;ng t&ocirc;i kh&ocirc;ng c&oacute; bất cứ điều khoản đảm bảo n&agrave;o đối với việc Người d&ugrave;ng sử dụng những thứ n&ecirc;u tr&ecirc;n sẽ kh&ocirc;ng vi phạm bất kỳ quyền ph&aacute;t minh, sở hữu, sở hữu tr&iacute; tuệ hoặc quy định kh&aacute;c.</span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">&bull; C&aacute;c Th&ocirc;ng tin của Trang web kh&ocirc;ng cấu th&agrave;nh một lời ch&agrave;o h&agrave;ng hay thuyết phục đối với bất kỳ ai. C&aacute;c th&ocirc;ng tin n&agrave;y cũng kh&ocirc;ng nhắm tới bất kỳ h&igrave;nh thức quảng c&aacute;o đầu tư, tư vấn, bu&ocirc;n b&aacute;n tranh hoặc tuy&ecirc;n truyền th&ocirc;ng tin n&agrave;o sai với quy định của ph&aacute;p luật nước CHXHCN Việt Nam. Nếu vi phạm, những Th&ocirc;ng tin đ&oacute; sẽ kh&ocirc;ng được xem l&agrave; th&ocirc;ng tin hợp ph&aacute;p.</span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">&bull; Việc sử dụng Trang web v&agrave; c&aacute;c nội dung của n&oacute; c&oacute; thể l&agrave; rủi ro ri&ecirc;ng của bất kỳ người truy cập n&agrave;o, bao gồm cả Người d&ugrave;ng. C&aacute;c th&ocirc;ng tin, v&agrave; cả những quan điểm của nội dung Trang web kh&ocirc;ng hề cấu th&agrave;nh sự thuyết phục hay lời ch&agrave;o h&agrave;ng của ch&uacute;ng t&ocirc;i về việc tư vấn, mua b&aacute;n c&aacute;c t&aacute;c phẩm hội họa cho bản th&acirc;n hoặc c&aacute;c đối tượng kh&aacute;c. Ngo&agrave;i ra, nội dung c&aacute;c bức tranh chỉ l&agrave; những quan điểm chủ quan của người cung cấp, do đ&oacute;, người sử dụng chỉ n&ecirc;n sử dụng c&aacute;c nội dung th&ocirc;ng tin tr&ecirc;n Trang web như th&ocirc;ng tin tham khảo.</span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">&bull; Ch&uacute;ng t&ocirc;i sẽ kh&ocirc;ng chịu tr&aacute;ch nhiệm ph&aacute;p l&yacute; trước những diễn đạt sai, xuy&ecirc;n tạc hay gian dối đối với bất kỳ th&ocirc;ng tin, sản phẩm hay dịch vụ n&agrave;o tr&ecirc;n Trang web, kể cả những dịch vụ tương tự được quảng c&aacute;o tr&ecirc;n Trang web.</span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">&bull; Ch&uacute;ng t&ocirc;i sẽ kh&ocirc;ng chịu tr&aacute;ch nhiệm nếu Người d&ugrave;ng sao ch&eacute;p, sửa chữa, đưa l&ecirc;n, tải về, gửi đi, (t&aacute;i) xuất bản, x&oacute;a đi hoặc nhập nhằng bất kỳ một vật phẩm sở hữu tr&iacute; tuệ n&agrave;o, kể cả việc Người d&ugrave;ng ph&acirc;n phối bất cứ dịch vụ hay nội dung n&agrave;o từ Trang web. Ch&uacute;ng t&ocirc;i sẽ kh&ocirc;ng chịu tr&aacute;ch nhiệm nếu Người d&ugrave;ng thay đổi nội dung hoặc sử dụng c&aacute;c nội dung v&agrave; dịch vụ m&agrave; họ lấy được từ Trang web để phục vụ bất kỳ mục đ&iacute;ch n&agrave;o ngo&agrave;i những mục đ&iacute;ch được cho ph&eacute;p. Nếu vi phạm nội dung của Điều khoản n&agrave;y, Người d&ugrave;ng đ&atilde; vi phạm sở hữu tr&iacute; tuệ bao gồm cả nh&atilde;n m&aacute;c, quyền sở hữu v&agrave; c&aacute;c sản phẩm đ&atilde; đăng k&yacute; độc quyền của ch&uacute;ng t&ocirc;i.</span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">&bull; To&agrave;n bộ Website v&agrave; c&aacute;c nội dung, th&ocirc;ng tin chi tiết cụ thể của Website thuộc quyền sở hữu của ch&uacute;ng t&ocirc;i, được bảo hộ theo ph&aacute;p luật của nước CHXHCN Việt Nam. Tuỳ thuộc y&ecirc;u cầu hoạt động, ch&uacute;ng t&ocirc;i c&oacute; thể điều chỉnh tổng thể việc sử dụng Website hoặc c&aacute;c nội dung của Website m&agrave; kh&ocirc;ng cần b&aacute;o trước. Trong những trường hợp n&agrave;y, ch&uacute;ng t&ocirc;i cũng kh&ocirc;ng chịu bất cứ tr&aacute;ch nhiệm g&igrave; đối với bất cứ thiệt hại n&agrave;o do việc thay đổi Website g&acirc;y ra.</span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">&bull; Những điều khoản từ chối tr&aacute;ch nhiệm n&agrave;y sẽ được &aacute;p dụng cho bất kỳ người n&agrave;o duyệt/truy cập Trang web v&agrave;/hoặc Người d&ugrave;ng tham gia v&agrave;o một thỏa thuận c&oacute; li&ecirc;n quan tới quyền lợi từ Dịch vụ do ch&uacute;ng t&ocirc;i cung cấp.</span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">&bull; Người sử dụng phải tu&acirc;n theo những hướng dẫn v&agrave; chỉ dẫn sử dụng Website một c&aacute;ch đầy đủ, đ&uacute;ng c&aacute;ch nhất v&agrave; kh&ocirc;ng được c&oacute; bất kỳ h&agrave;nh vi n&agrave;o tr&aacute;i ph&aacute;p luật, tr&aacute;i đạo đức trong việc sử dụng Website. Trong qu&aacute; tr&igrave;nh sử dụng Website, nếu người sử dụng c&oacute; bất cứ h&agrave;nh vi n&agrave;o g&acirc;y thiệt hại cho Website hay cho ch&uacute;ng t&ocirc;i hoặc đ&oacute; l&agrave; những h&agrave;nh vi tr&aacute;i ph&aacute;p luật, đạo đức x&atilde; hội th&igrave; người sử dụng phải chịu ho&agrave;n to&agrave;n tr&aacute;ch nhiệm trước ph&aacute;p luật v&agrave; bị xử l&yacute; theo quy định của ph&aacute;p luật. Ch&uacute;ng t&ocirc;i c&oacute; to&agrave;n quyền thực hiện những biện ph&aacute;p cần thiết theo c&aacute;ch hiểu của ch&uacute;ng t&ocirc;i để ngăn chặn v&agrave; loại bỏ những h&agrave;nh vi sai tr&aacute;i n&agrave;y.</span></p>\r\n\r\n<p style="text-align: justify;"><strong>C&aacute;c điều khoản kh&aacute;c</strong></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">&bull; Trong trường hợp bất kỳ điều n&agrave;o trong những Điều khoản v&agrave; kiều kiện n&agrave;y bị một t&ograve;a &aacute;n đủ thẩm quyền xem l&agrave; kh&ocirc;ng c&oacute; hiệu lực, bất hợp ph&aacute;p hoặc kh&ocirc;ng thể &aacute;p dụng, điều đ&oacute; sẽ được coi như bị loại bỏ khỏi những Điều khoản v&agrave; điều kiện đ&atilde; n&ecirc;u, v&agrave; những Điều khoản v&agrave; điều kiện c&ograve;n lại sẽ vẫn giữ nguy&ecirc;n hiệu lực &aacute;p dụng.</span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">&bull; C&aacute;c Điều khoản miễn tr&aacute;ch đối với ch&uacute;ng t&ocirc;i sẽ được &aacute;p dụng cho bất kỳ người n&agrave;o duyệt/truy cập Trang web v&agrave;/hoặc tham gia một thỏa thuận li&ecirc;n quan tới những lợi &iacute;ch ph&aacute;t sinh từ Dịch vụ do ch&uacute;ng t&ocirc;i cung cấp.</span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">&bull; Bản điều khoản sử dụng điều chỉnh c&aacute;c Dịch vụ ri&ecirc;ng biệt sẽ được đăng tải tr&ecirc;n c&aacute;c trang cụ thể d&agrave;nh ri&ecirc;ng cho c&aacute;c Dịch vụ li&ecirc;n quan tr&ecirc;n Trang web. Nếu Kh&aacute;ch giao dịch/sử dụng bất kỳ Dịch vụ n&agrave;o, họ sẽ được xem như đ&atilde; chấp thuận c&aacute;c Điều khoản li&ecirc;n quan tới c&aacute;c sản phẩm đ&oacute; v&agrave; đồng thời, c&aacute;c Điều khoản trong văn bản n&agrave;y.</span></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">&bull; Ch&uacute;ng t&ocirc;i c&oacute; quyền x&acirc;y dựng, sửa đổi, bổ sung quy định sử dụng Website hoặc quy định sử dụng từng nội dung cụ thể của Website tại bất cứ thời điểm n&agrave;o theo y&ecirc;u cầu của việc quản l&yacute; v&agrave; vận h&agrave;nh Website. Những quy định sử dụng mới n&agrave;y sẽ c&oacute; hiệu lực ngay lập tức khi được đăng tải tr&ecirc;n Website v&agrave; được hiểu l&agrave; đ&atilde; c&oacute; sự đồng &yacute; của người sử dụng khi người sử dụng truy cập Website.</span></p>\r\n\r\n<p><strong>Luật &aacute;p dụng v&agrave; Thẩm quyền</strong></p>\r\n\r\n<p style="text-align: justify;"><span style="font-size:16px;">C&aacute;c Điều khoản sử dụng n&agrave;y được điều chỉnh v&agrave; diễn giải theo luật ph&aacute;p Việt Nam. Mọi tranh chấp ph&aacute;t sinh li&ecirc;n quan tới c&aacute;c Điều khoản sử dụng n&agrave;y hoặc bất kỳ tranh chấp ph&aacute;t sinh li&ecirc;n quan tới Trang web, đều c&oacute; thể đệ tr&igrave;nh l&ecirc;n c&aacute;c t&ograve;a &aacute;n c&oacute; thẩm quyền tại H&agrave; Nội, Việt Nam để giải quyết.</span></p>\r\n', '2015-01-08 11:14:06', '2015-01-11 09:45:46'),
(3, 'Liên hệ với chúng tôi', '<p style="text-align: justify;">Nếu bạn c&oacute; bất kỳ c&acirc;u hỏi, đề xuất n&agrave;o để l&agrave;m cho trang web được tốt hơn hoặc gặp bất kỳ sự cố g&igrave;, xin vui l&ograve;ng li&ecirc;n hệ với ch&uacute;ng t&ocirc;i.<br />\r\n<strong>Nh&oacute;m 12 - Lớp 2C-11, Khoa C&ocirc;ng nghệ Th&ocirc;ng tin<br />\r\nPh&ograve;ng 112, Nh&agrave; C, Trường Đại học H&agrave; Nội<br />\r\nĐịa chỉ: Km 9, đường Nguyễn Tr&atilde;i, quận Thanh Xu&acirc;n, t.phố H&agrave; Nội.<br />\r\nĐiện thoại: 043 854 4338<br />\r\nEmail: <a href="mailto:2c11.hanu@gmail.com?subject=Li%C3%AAn%20h%E1%BB%87%20v%E1%BB%9Bi%202C-11&amp;body=Xin%20ch%C3%A0o!">2c11.hanu@gmail.com</a></strong></p>\r\n', '2015-01-11 09:57:28', '2015-01-11 10:04:41');

